import React, { useState, useEffect } from 'react';
import styles from '../styles/app.module.scss'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeartbeat, faCheckCircle, faLightbulb, faArrowLeft, faArrowRight, faEdit, faPlus, faCheck } from '@fortawesome/free-solid-svg-icons'

const App = () => {

  const [allTasks, setAllTasks] = useState([

    {
      taskName: "abasadjfhj",
      taskDesc: "this is desc",
      taskTarget: "later",
    }
  ]
);

  const targets = ["todo","done","later"];

  let gotTargetForTodo  = false;
  let gotTargetForDone  = false;
  let gotTargetForLater = false;

  return (
    <div className={styles.wrapper}>

      <div className={styles.container}>
        <div className={styles.todo}>
          <div className={styles.todo__header}>
            <div className={styles.header_content}>
              <div className={styles.header_content__title}>
                <FontAwesomeIcon icon={faHeartbeat} className={styles.headerIcon}/> To-Do
              </div>
              <div className={styles.header_content__count}>
                5
              </div>
            </div>
          </div>
          <div className={styles.todo__content}>

              {/* Start of cards */}

              {
                allTasks.map(function(task, index){
                  // Show only the targeted tasks.
                  if(task.taskTarget == targets[0]){
                    gotTargetForTodo = true;
                    return <div className={styles.card}>
                      {/* Controls */}
                      <div className={styles.card__controls}>
                        <a href='#' className={styles.navIcon}><FontAwesomeIcon icon={faArrowLeft}/></a>
                        <a href='#' className={styles.navIcon}><FontAwesomeIcon icon={faArrowRight} /></a>
                        <a href='#'><FontAwesomeIcon icon={faEdit} /></a>
                      </div>
                      {/* End of Controls */}
                      <div className={styles.card__title}>
                        {task.taskName}
                      </div>
                      <div className={styles.card__desc}>
                        {task.taskDesc}
                      </div>
                    </div>
                  }
                })
              } 

              {() => {
                if(!gotTargetForTodo){
                  return <div>Nono</div>
                }
              }}

              {/* End of cards */}

              <div className={styles.cardAdd}>
                <a href='#'><FontAwesomeIcon icon={faPlus} /></a>
              </div>
          </div>
        </div>
        <div className={styles.done}>
          <div className={styles.done__header}>
            <div className={styles.header_content}>
              <div className={styles.header_content__title}>
                <FontAwesomeIcon icon={faCheckCircle} className={styles.headerIcon}/> Done
              </div>
              <div className={styles.header_content__count}>
                5
              </div>
            </div>
          </div>
          <div className={styles.done__content}>
              <div className={styles.cardAdd}>
                <a href='#'><FontAwesomeIcon icon={faPlus} /></a>
              </div>

              {/* AddTask banner */}

              <div className={styles.cardBanner}>
                <div className={styles.cardBanner__icon}>
                  <div className={styles.banner_icon}>
                    <FontAwesomeIcon icon={faCheck}/>
                  </div>
                </div>
                <div className={styles.cardBanner__texts}>
                  <div className={styles.banner_title}>
                    No Tasks
                  </div>
                  <div className={styles.banner_desc}>
                    Move tasks by navigators <br/>or click + to add new task
                  </div>
                </div>
              </div>

              {/* end of AddTask banner */}

          </div>
        </div>
        <div className={styles.later}>
          <div className={styles.later__header}>
            <div className={styles.header_content}>
              <div className={styles.header_content__title}>
                <FontAwesomeIcon icon={faLightbulb} className={styles.headerIcon}/> To-Do Someday
              </div>
              <div className={styles.header_content__count}>
                5
              </div>
            </div>
          </div>
          <div className={styles.later__content}>

              {/* Start of cards */}

              {
                allTasks.map(function(task, index){
                  // Show only the targeted tasks.
                  if(task.taskTarget == targets[2]){
                    return <div className={styles.card}>
                      {/* Controls */}
                      <div className={styles.card__controls}>
                        <a href='#' className={styles.navIcon}><FontAwesomeIcon icon={faArrowLeft}/></a>
                        <a href='#' className={styles.navIcon}><FontAwesomeIcon icon={faArrowRight} /></a>
                        <a href='#'><FontAwesomeIcon icon={faEdit} /></a>
                      </div>
                      {/* End of Controls */}
                      <div className={styles.card__title}>
                        {task.taskName}
                      </div>
                      <div className={styles.card__desc}>
                        {task.taskDesc}
                      </div>
                    </div>
                  }
                })
              } 

              {/* End of cards */}

              <div className={styles.cardAdd}>
                <a href='#'><FontAwesomeIcon icon={faPlus} /></a>
              </div>
          </div>
        </div>
      </div>

      <style global jsx>{`
        html,
        body,
        body > div:first-child,
        div#__next,
        div#__next > div{
          height: 100%;
        }
      `}</style>
    </div>


  )
}

export default App
