                  <div className={styles.card__title_skeleton}></div>
                  <div className={styles.card__desc_skeleton}></div> 
                </div> : <React.Fragment>
                <div className={styles.cardAdd}>
                  <a href='#' onClick={() => {addNewTask(targets[0])}}><FontAwesomeIcon icon={faPlus} /></a>
